import React, { Component } from 'react';

import './item-list.css';
import SwapiService from "../../services/swapi-service";
import Spinner from "../spinner";

export default class ItemList extends Component {

    swapiService = new SwapiService();

    state = {
        peopleList: null
    };

    componentDidMount() {
        this.swapiService.getAllPerson().then((peopleList) => {
                this.setState({
                    peopleList
                });
            });
    }

    renderItems(arr) {
        return arr.map(({id, name}) => {
            return (
                <tr className="d-flex"
                key={id}
                onClick={() => this.propsOnItemSelected(id)}>
                    {name}
                </tr>
            );
        });
    }

    render() {

        const { peopleList } = this.state;

        if (!peopleList) {
            return <Spinner/>;
        }

        const items = this.renderItems()

        return (
            <div className="item-list card mt-3">
                <table className="table">
                    <tr className="d-flex">
                        <th className="w-100">Luke Skywalker</th>
                    </tr>
                    <tr className="d-flex">
                        <th className="w-100">Darth Vader</th>
                    </tr>
                    <tr className="d-flex">
                        <th className="w-100">R2-D2</th>
                    </tr>
                </table>
            </div>
        )
    }
};
